                        sqlcon.Execute("UPDATE SalesMaster SET BalanceDue=@BalanceDue,Status=@Status,CashAmount=@CashAmount,CreditAmount=@CreditAmount where SalesMasterId=@SalesMasterId", para, sql, 0, CommandType.Text);
