        {
            var view = purchaseRepository.GetPreviousDuesBalanceSales(mster.LedgerId);
            PreviousDue = view.DueBalance;
            articleModel = purchaseRepository.GetCustomerPaymentList(mster.LedgerId);
            foreach (var item in articleModel)
            {
                decTotalDue = decTotalDue + item.TotalDue;
                decTotal = decTotal + item.GrandTotal;
            }
        }
        else
        {
            PreviousDue = 0;
            articleModel.Clear();
        }
